mapper.select:
		select
		*
		from
		tbl_member
		
			
				
					uid like CONCAT('%',#{keyword},'%')
					limit
					#{pageStart}, #{perPageNum}
				
				
					uname like CONCAT('%',#{keyword},'%')
					limit
					#{pageStart}, #{perPageNum}
				
				
					uemail like CONCAT('%',#{keyword},'%')
					limit
					#{pageStart}, #{perPageNum}
				
				
					ugender = #{keyword}
					limit
					#{pageStart}, #{perPageNum}
				
				
					unick like CONCAT('%',#{keyword},'%')
					limit
					#{pageStart}, #{perPageNum}
				
				
					ubirth like CONCAT('%',#{keyword},'%')
					limit
					#{pageStart}, #{perPageNum}
				
				
					ustatus =#{keyword}
					limit
					#{pageStart}, #{perPageNum}

mapper.insert:
		insert into
		tbl_member
		(uid, unick,upw, uname, uemail,
		ugender, ubirth)
		values(#{uid},#{unick},#{upw},#{uname},#{uemail},#{ugender},#{ubirth}
		)

mapper.update:
		update
		tbl_member
		set
		uname=#{uname},
		ugender=#{ugender},
		ubirth=#{ubirth}
		where uid=#{uid}
		
			
				and ustatus=#{ustatus}
			
			
				and ustatus=#{ustatus}

update tbl_member set
ustatus =#{ustatus}
where
uid
=#{uid}
